import { Component } from "react";

import Cookies from "js-cookie";

import Loader from "react-loader-spinner";

class ProductItemDetails extends Component {
  state = { specificProduct: {}, similarProducts: [] };
  componentDidMount() {
    const { match } = this.props;
    const { params } = match;
    // console.log(params.id);
    this.getSpecifiedProduct(params.id);
  }

  covertToCamelCase = (data) => {
    return {
      availability: data.availability,
      brand: data.brand,
      description: data.description,
      imageUrl: data.image_url,
      price: data.price,
      rating: data.rating,
      title: data.title,
      totalReviews: data.total_reviews,
    };
  };
  getSpecifiedProduct = async (id) => {
    const jwtToken = Cookies.get("jwt_token");
    const apiUrl = `https://apis.ccbp.in/products/${id}`;
    const options = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
      method: "GET",
    };
    const response = await fetch(apiUrl, options);
    if (response.ok === true) {
      const data = await response.json();
      console.log(data);
      const updatedSpecificProduct = this.covertToCamelCase(data);
      const updatedSimilarProductsList = data.similar_products.map((product) =>
        this.covertToCamelCase(product)
      );
      this.setState({
        specificProduct: updatedSpecificProduct,
        similarProducts: updatedSimilarProductsList,
      });
    } else {
      console.log("status fail");
    }
  };
  loadingView = () => (
    <div>
      <Loader />
    </div>
  );
  failureView = () => (
    <div>
      <img
        src="https://assets.ccbp.in/frontend/react-js/nxt-trendz-error-view-img.png"
        alt="error view"
      />
    </div>
  );
  successView = () => {
    const { specificProduct, similarProducts } = this.state;

    return (
      <div>
        <div>
          <img src={specificProduct.imageUrl} alt={specificProduct.title} />
          <div>
            <h1>{specificProduct.title}</h1>
            <p>{specificProduct.price}</p>
            <p>
              {specificProduct.rating}
              <img
                src="https://assets.ccbp.in/frontend/react-js/star-img.png"
                alt="star"
              />
            </p>
            <p>{specificProduct.description}</p>
          </div>
        </div>
      </div>
    );
  };
  render() {
    // const { specificProduct, similarProducts } = this.state;
    // console.log(specificProduct);
    // console.log(similarProducts);
    return (
      <div>
        <h1>Hello</h1>
      </div>
    );
  }
}

export default ProductItemDetails;
