import { Component } from "react";

import ProductCard from "../ProductCard";

import ProductsHeader from "../ProductsHeader";

import FiltersGroup from "../FiltersGroup";

import Cookies from "js-cookie";

import Loader from "react-loader-spinner";

const categoryOptions = [
  {
    name: "Clothing",
    categoryId: "1",
  },
  {
    name: "Electronics",
    categoryId: "2",
  },
  {
    name: "Appliances",
    categoryId: "3",
  },
  {
    name: "Grocery",
    categoryId: "4",
  },
  {
    name: "Toys",
    categoryId: "5",
  },
];

const ratingsList = [
  {
    ratingId: "4",
    imageUrl:
      "https://assets.ccbp.in/frontend/react-js/rating-four-stars-img.png",
  },
  {
    ratingId: "3",
    imageUrl:
      "https://assets.ccbp.in/frontend/react-js/rating-three-stars-img.png",
  },
  {
    ratingId: "2",
    imageUrl:
      "https://assets.ccbp.in/frontend/react-js/rating-two-stars-img.png",
  },
  {
    ratingId: "1",
    imageUrl:
      "https://assets.ccbp.in/frontend/react-js/rating-one-star-img.png",
  },
];

const apiStatusObj = {
  initial: "INITIAL",
  success: "SUCCESS",
  failure: "FAILURE",
  inProgress: "INPROGRESS",
};

const sortbyOptions = [
  {
    optionId: "PRICE_HIGH",
    displayText: "Price (High-Low)",
  },
  {
    optionId: "PRICE_LOW",
    displayText: "Price (Low-High)",
  },
];

class AllProductsSection extends Component {
  state = {
    productList: [],
    apiStatus: apiStatusObj.initial,
    sortByValue: sortbyOptions[0].optionId,
    searchValue: "",
    category: "",
    rating: "",
  };

  componentDidMount() {
    this.getAllProducts();
  }

  updateSortByValue = (option) => {
    console.log(option);
    this.setState({ sortByValue: option }, this.getAllProducts);
  };
  userSearchInput = (search) => {
    console.log(search);
    this.setState({ searchValue: search }, this.getAllProducts);
  };
  userCategoryInput = (category) => {
    console.log(category);
    this.setState({ category }, this.getAllProducts);
  };
  userRatingInput = (rating) => {
    console.log(rating);
    this.setState({ rating }, this.getAllProducts);
  };

  clearAllFilters = () => {
    this.setState({
      apiStatus: apiStatusObj.inProgress,
      category: "",
      rating: "",
      searchValue: "",
      sortByValue: sortbyOptions[0].optionId,
    });
  };
  getAllProducts = async () => {
    this.setState({ apiStatus: apiStatusObj.inProgress });
    const { sortByValue, searchValue, category, rating } = this.state;
    const jwtToken = Cookies.get("jwt_token");
    const options = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
      method: "GET",
    };
    const response = await fetch(
      `https://apis.ccbp.in/products?sort_by=${sortByValue}&category=${category}&title_search=${searchValue}&rating=${rating}`,
      options
    );
    //https://apis.ccbp.in/products?sort_by=PRICE_HIGH&category=4&title_search=machine&rating=4
    if (response.ok === true) {
      const data = await response.json();
      console.log(data.products);

      const filteredData = data.products.map((eachProducts) => ({
        title: eachProducts.title,
        brand: eachProducts.brand,
        price: eachProducts.price,
        id: eachProducts.id,
        imageUrl: eachProducts.image_url,
        ratings: eachProducts.ratings,
      }));

      this.setState({
        productList: filteredData,
        apiStatus: apiStatusObj.success,
      });
    } else if (response.status === 401) {
      this.setState({ apiStatus: apiStatusObj.failure });
    }
  };

  showLoading = () => (
    <div>
      <Loader />
    </div>
  );
  emptyView = () => (
    <div>
      <img
        src="https://assets.ccbp.in/frontend/react-js/nxt-trendz/nxt-trendz-no-products-view.png"
        alt="no products"
      />
      <h1>No Products Found</h1>
      <p>We could not find any products,Try other filters</p>
    </div>
  );
  successView = () => {
    const { productList } = this.state;
    const emptyList = productList.length < 1;
    return (
      <div>
        <ProductsHeader
          sortbyOptions={sortbyOptions}
          updateSortByValue={this.updateSortByValue}
          userSearchInput={this.userSearchInput}
        />
        {emptyList ? (
          <>{this.emptyView()}</>
        ) : (
          <>
            <h1>All Products</h1>
            <ul>
              {productList.map((eachProduct) => (
                <ProductCard
                  productDetails={eachProduct}
                  key={eachProduct.id}
                />
              ))}
            </ul>
          </>
        )}
      </div>
    );
  };

  failureView = () => (
    <div>
      <img
        src="https://assets.ccbp.in/frontend/react-js/nxt-trendz/nxt-trendz-products-error-view.png"
        alt="products failure"
      />
    </div>
  );

  showViews = () => {
    const { apiStatus } = this.state;
    switch (apiStatus) {
      case apiStatusObj.inProgress:
        return this.showLoading();
      case apiStatusObj.success:
        return this.successView();
      case apiStatusObj.failure:
        return this.failureView();
      default:
        return null;
    }
  };
  render() {
    return (
      <div>
        <FiltersGroup
          ratingsList={ratingsList}
          categoryOptions={categoryOptions}
          userCategoryInput={this.userCategoryInput}
          userRatingInput={this.userRatingInput}
          clearAllFilters={this.clearAllFilters}
        />
        <div>{this.showViews()}</div>
      </div>
    );
  }
}

export default AllProductsSection;
