import "./index.css";

const RatingImage = (props) => {
  const { ratingDetails, selectedRating } = props;
  const { ratingId, imageUrl } = ratingDetails;

  const sendId = () => {
    selectedRating(ratingId);
  };

  return (
    <li>
      <button type="button" onClick={sendId}>
        <img src={imageUrl} alt={`rating ${ratingId}`} />
        &up
      </button>
    </li>
  );
};

const CategorySelect = (props) => {
  const { categoryDetails, selectedCategory } = props;
  const { categoryId, name } = categoryDetails;

  const sendId = () => {
    selectedCategory(categoryId);
  };

  return (
    <li>
      <button type="button" onClick={sendId}>
        <p>{name}</p>
      </button>
    </li>
  );
};

const FiltersGroup = (props) => {
  const {
    ratingsList,
    categoryOptions,
    userCategoryInput,
    userRatingInput,
    clearAllFilters,
    userSearchInput,
    eventKey,
    searchValue,
  } = props;

  const onClickCategory = (id) => {
    console.log(id);
    userCategoryInput(id);
  };
  const onClickRatings = (id) => {
    userRatingInput(id);
  };
  const onClickClearFilters = (event) => {
    clearAllFilters();
  };

  const onChangeSearchInput = (event) => {
    userSearchInput(event.target.value);
  };
  const enterKeyDown = (event) => {
    if (event.key === "Enter") {
      eventKey();
    }
  };
  return (
    <div>
      <div>
        <input
          type="search"
          value={searchValue}
          onKeyDown={enterKeyDown}
          onChange={onChangeSearchInput}
        />
      </div>
      <p>Categories</p>
      <ul>
        {categoryOptions.map((eachCate) => (
          <CategorySelect
            categoryDetails={eachCate}
            key={eachCate.categoryId}
            selectedCategory={onClickCategory}
          />
        ))}
      </ul>
      <p>Ratings</p>
      <div>
        <ul>
          {ratingsList.map((eachRat) => (
            <RatingImage
              ratingDetails={eachRat}
              selectedRating={onClickRatings}
              key={eachRat.ratingId}
            />
          ))}
        </ul>
      </div>
      <div>
        <button type="button" onClick={onClickClearFilters}>
          Clear All
        </button>
      </div>
    </div>
  );
};

export default FiltersGroup;
