// Write your JS code here
import { Component, Redirect } from "react";

import Cookies from "js-cookie";

import "./index.css";

class LoginForm extends Component {
  state = { username: "", password: "", showError: false, errorMsg: "" };

  onChangeUsername = (event) => {
    this.setState({ username: event.target.value });
  };

  onChangePassword = (event) => {
    this.setState({ password: event.target.value });
  };
  successfulAuthorization = (jwt_token) => {
    const { history } = this.props;
    Cookies.set("jwt_token", jwt_token, { expires: 30 });
    history.replace("/");
  };

  failureAuthorization = (data) => {
    this.setState({ showError: true, errorMsg: data.error_msg });
  };
  SubmitForm = async (event) => {
    event.preventDefault();
    const { username, password } = this.state;
    const userDetails = { username, password };
    const options = {
      method: "POST",
      body: JSON.stringify(userDetails),
    };
    const response = await fetch("https://apis.ccbp.in/login", options);
    const data = await response.json();

    if (response.ok === true) {
      this.successfulAuthorization(data.jwt_token);
    } else {
      this.failureAuthorization(data);
    }
  };
  render() {
    const { username, password, showError, errorMsg } = this.state;
    const jwtToken = Cookies.get("jwt_token");
    if (jwtToken !== undefined) {
      return <Redirect to="/" />;
    }
    return (
      <div>
        <div>
          <img
            src="https://assets.ccbp.in/frontend/react-js/nxt-trendz-login-img.png"
            alt="website login"
            className="loginImage"
          />
        </div>
        <div>
          <div>
            <img
              src="https://assets.ccbp.in/frontend/react-js/nxt-trendz-logo-img.png"
              alt="website logo"
            />
            <form onSubmit={this.SubmitForm}>
              <label htmlFor="username">USERNAME</label>
              <input
                id="username"
                type="text"
                placeholder="Username"
                onChange={this.onChangeUsername}
                value={username}
              />
              <label htmlFor="password">PASSWORD</label>
              <input
                id="password"
                type="password"
                placeholder="Password"
                onChange={this.onChangePassword}
                value={password}
              />
              <button type="submit">Login</button>
              {showError && <p>{errorMsg}</p>}
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default LoginForm;
