const FiltersGroup = (props) => {
  const {
    ratingsList,
    categoryOptions,
    userCategoryInput,
    userRatingInput,
    clearAllFilters,
  } = props;

  const onClickCategory = (event) => {
    userCategoryInput(event.target.value);
  };
  const onClickRatings = (event) => {
    // console.log(event.target.image);
    userRatingInput();
  };
  const onClickClearFilters = (event) => {
    clearAllFilters();
  };
  return (
    <div>
      <p>Categories</p>
      <ul>
        {categoryOptions.map((eachCate) => (
          <li key={eachCate.categoryId}>
            <button
              type="button"
              onClick={onClickCategory}
              value={eachCate.categoryId}
            >
              {eachCate.name}
            </button>
          </li>
        ))}
      </ul>
      <p>Ratings</p>
      <div>
        <ul>
          {ratingsList.map((eachRat) => (
            <li key={eachRat.ratingId} onClick={onClickRatings}>
              <button value={eachRat.ratingId}>
                <p>
                  <img
                    src={eachRat.imageUrl}
                    alt={`rating ${eachRat.ratingId}`}
                  />
                </p>
              </button>
            </li>
          ))}
        </ul>
      </div>
      <div>
        <button type="button" onClick={onClickClearFilters}>
          Clear All
        </button>
      </div>
    </div>
  );
};

export default FiltersGroup;
