const ProductHeader = (props) => {
  const { sortbyOptions, updateSortByValue, userSearchInput } = props;

  const onChangeOption = (event) => updateSortByValue(event.target.value);
  const onChangeSearchInput = (event) => {
    let search;
    if (event.target.value === "enter") {
      userSearchInput(event.target.value);
    } else {
      search = event.target.value;
    }
  };
  return (
    <div>
      <div>
        <input type="search" onChange={onChangeSearchInput} />
      </div>
      <div>
        <p>Sort by</p>
        <select onChange={onChangeOption}>
          {sortbyOptions.map((eachOption) => (
            <option value={eachOption.optionId} key={eachOption.optionId}>
              {eachOption.displayText}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default ProductHeader;
