import CartContext from "../../context/CartContext";

import Popup from "reactjs-popup";

import PaymentOptions from "../PaymentOptions";

import "./index.css";

const listOfOptions = [
  { id: "UPI", value: "UPI", displayText: "UPI" },
  { id: "NetBanking", value: "NetBanking", displayText: "Net Banking" },
  { id: "Wallet", value: "Wallet", displayText: "Wallet" },
  { id: "Card", value: "Card", displayText: "Card" },
  { id: "COD", value: "COD", displayText: "Cash On Delivery" },
];

const CartSummary = () => (
  <CartContext.Consumer>
    {(value) => {
      const { cartList } = value;
      let totalBill = 0;
      cartList.forEach(
        (eachItem) => (totalBill += eachItem.price * eachItem.quantity)
      );
      const boolean = null;
      const UserPaymentOption = (option) => {
        if (option === "COD") {
          boolean = true;
        } else {
          boolean = false;
        }
      };
      return (
        <div>
          <h1>Order Total: Rs{totalBill}</h1>
          <p>{cartList.length}Items in cart</p>
          <div>
            <Popup
              modal
              className="popup-overlay"
              trigger={<button>Check out</button>}
              position="center center"
            >
              <ul className="payment-list">
                {listOfOptions.map((item) => (
                  <PaymentOptions key={item.id} details={item} />
                ))}
              </ul>
            </Popup>
          </div>
        </div>
      );
    }}
  </CartContext.Consumer>
);

export default CartSummary;

