const RatingImage = (props) => {
  const { ratingDetails, selectedRating } = props;
  const { ratingId, imageUrl } = ratingDetails;

  const sendId = () => {
    selectedRating(ratingId);
  };

  return (
    <li>
      <button type="button" onClick={sendId}>
        <img src={imageUrl} alt={`rating ${ratingId}`} />
      </button>
    </li>
  );
};

const FiltersGroup = (props) => {
  const {
    ratingsList,
    categoryOptions,
    userCategoryInput,
    userRatingInput,
    clearAllFilters,
  } = props;

  const onClickCategory = (event) => {
    userCategoryInput(event.target.value);
  };
  const onClickRatings = (event) => {
    console.log(event);
    userRatingInput();
  };
  const onClickClearFilters = (event) => {
    clearAllFilters();
  };
  return (
    <div>
      <p>Categories</p>
      <ul>
        {categoryOptions.map((eachCate) => (
          <li key={eachCate.categoryId}>
            <button
              type="button"
              onClick={onClickCategory}
              value={eachCate.categoryId}
            >
              {eachCate.name}
            </button>
          </li>
        ))}
      </ul>
      <p>Ratings</p>
      <div>
        <ul>
          {ratingsList.map((eachRat) => (
            <RatingImage
              ratingDetails={eachRat}
              selectedRating={onClickRatings}
              key={eachRat.ratingId}
            />
          ))}
        </ul>
      </div>
      <div>
        <button type="button" onClick={onClickClearFilters}>
          Clear All
        </button>
      </div>
    </div>
  );
};

export default FiltersGroup;
