import { Component } from "react";

class ProductHeader extends Component {
  state = { userInput: "" };
  onChangeOption = (event) => {
    const { updateSortByValue } = this.props;
    updateSortByValue(event.target.value);
  };
  onChangeSearchInput = (event) => {
    this.setState({ userInput: event.target.value });
  };
  render() {
    const { sortbyOptions, userSearchInput } = this.props;
    const { userInput } = this.state;

    return (
      <div>
        <div>
          <input type="search" onChange={this.onChangeSearchInput} />
        </div>
        <div>
          <p>Sort by</p>
          <select onChange={this.onChangeOption}>
            {sortbyOptions.map((eachOption) => (
              <option value={eachOption.optionId} key={eachOption.optionId}>
                {eachOption.displayText}
              </option>
            ))}
          </select>
        </div>
      </div>
    );
  }
}

