import CartContext from "../../context/CartContext";

import Popup from "reactjs-popup";

import "./index.css";

const CartSummary = () => (
  <CartContext.Consumer>
    {(value) => {
      const { cartList } = value;
      let totalBill = 0;
      cartList.forEach(
        (eachItem) => (totalBill += eachItem.price * eachItem.quantity)
      );
      return (
        <div>
          <h1>Order Total: Rs{totalBill}</h1>
          <p>{cartList.length}Items in cart</p>
          <div>
            <Popup
              modal
              className="popup-overlay"
              trigger={<button>Check out</button>}
              position="center center"
            >
              <div>
                <input
                  type="Radio"
                  value="UPI"
                  id="UPI"
                  name="payment"
                  className="input"
                />
                <label htmlFor="UPI">UPI</label>
              </div>

              <input type="Radio" value="card" id="card" name="payment" />
              <label htmlFor="card">Card</label>

              <input
                type="Radio"
                value="net-banking"
                id="netBanking"
                name="payment"
              />
              <label htmlFor="netBanking">Net Banking</label>

              <input type="Radio" value="wallet" id="wallet" name="payment" />
              <label htmlFor="wallet">Wallet</label>

              <input type="radio" value="COD" id="cod" name="payment" />
              <label htmlFor="cod">Cash On Delivery</label>
            </Popup>
          </div>
        </div>
      );
    }}
  </CartContext.Consumer>
);

export default CartSummary;

