const FiltersGroup = (props) => {
  const {
    ratingsList,
    categoryOptions,
    userCategoryInput,
    userRatingInput,
    clearAllFilters,
    rating,
  } = props;

  const onClickCategory = (event) => {
    console.log(event.target);
    userCategoryInput(event.target.value);
  };
  const onClickRating = (event) => {
    console.log(event.target);
    userRatingInput(event.target.value);
  };
  const onClickClearFilters = (event) => {
    clearAllFilters();
  };
  return (
    <div>
      <p>Categories</p>
      <ul>
        {categoryOptions.map((eachCate) => (
          <li key={eachCate.categoryId}>
            <button
              type="button"
              onClick={onClickCategory}
              value={eachCate.categoryId}
            >
              {eachCate.name}
            </button>
          </li>
        ))}
      </ul>
      <p>Ratings</p>
      <div>
        <ul>
          {ratingsList.map((eachRat) => (
            <li key={eachRat.ratingId}>
              <button type="button" onClick={onClickRating}>
                <img
                  src={eachRat.imageUrl}
                  alt={`rating ${eachRat.ratingId}`}
                />
              </button>
            </li>
          ))}
        </ul>
      </div>
      <div>
        <button type="button" onClick={onClickClearFilters}>
          Clear All
        </button>
      </div>
    </div>
  );
};

export default FiltersGroup;
