import { Component } from "react";
import Loader from "react-loader-spinner";
import Cookies from "js-cookie";

import FiltersGroup from "../FiltersGroup";
import ProductCard from "../ProductCard";
import ProductsHeader from "../ProductsHeader";

// import './index.css'

const apiStatusObj = {
  initial: "INITIAL",
  success: "SUCCESS",
  failure: "FAILURE",
  inProgress: "INPROGRESS",
};

const categoryOptions = [
  {
    name: "Clothing",
    categoryId: "1",
  },
  {
    name: "Electronics",
    categoryId: "2",
  },
  {
    name: "Appliances",
    categoryId: "3",
  },
  {
    name: "Grocery",
    categoryId: "4",
  },
  {
    name: "Toys",
    categoryId: "5",
  },
];

const sortbyOptions = [
  {
    optionId: "PRICE_HIGH",
    displayText: "Price (High-Low)",
  },
  {
    optionId: "PRICE_LOW",
    displayText: "Price (Low-High)",
  },
];

const ratingsList = [
  {
    ratingId: "4",
    imageUrl:
      "https://assets.ccbp.in/frontend/react-js/rating-four-stars-img.png",
  },
  {
    ratingId: "3",
    imageUrl:
      "https://assets.ccbp.in/frontend/react-js/rating-three-stars-img.png",
  },
  {
    ratingId: "2",
    imageUrl:
      "https://assets.ccbp.in/frontend/react-js/rating-two-stars-img.png",
  },
  {
    ratingId: "1",
    imageUrl:
      "https://assets.ccbp.in/frontend/react-js/rating-one-star-img.png",
  },
];

class AllProductsSection extends Component {
  state = {
    productsList: [],
    apiStatus: apiStatusObj.initial,
    activeOptionId: sortbyOptions[0].optionId,
    category: "",
    rating: "",
    searchValue: "",
  };

  componentDidMount() {
    this.getProducts();
  }

  getProducts = async () => {
    this.setState({
      apiStatus: apiStatusObj.inProgress,
    });
    const jwtToken = Cookies.get("jwt_token");

    // TODO: Update the code to get products with filters applied

    const { activeOptionId, category, searchValue, rating } = this.state;
    console.log(category);
    console.log(activeOptionId);
    console.log(searchValue);
    console.log(rating);
    const apiUrl = `https://apis.ccbp.in/products?sort_by=${activeOptionId}&category=${category}&title_search=${searchValue}&rating=${rating}`;
    const options = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
    };
    const response = await fetch(apiUrl, options);
    if (response.ok) {
      const fetchedData = await response.json();
      const updatedData = fetchedData.products.map((product) => ({
        title: product.title,
        brand: product.brand,
        price: product.price,
        id: product.id,
        imageUrl: product.image_url,
        rating: product.rating,
      }));
      this.setState({
        productsList: updatedData,
        apiStatus: apiStatusObj.success,
      });
    } else {
      this.setState({ apiStatus: apiStatusObj.failure });
    }
  };

  changeSortby = (activeOptionId) => {
    this.setState({ activeOptionId }, this.getProducts);
  };

  userCategoryInput = (category) => {
    console.log(category);
    this.setState({ category }, this.getProducts);
  };

  userRatingInput = (rating) => {
    console.log(rating);
    this.setState({ rating }, this.getProducts);
  };

  userSearchInput = (search) => {
    console.log(search);
    this.setState({ searchValue: search }, this.getProducts);
  };

  clearAllFilters = () => {
    this.setState(
      {
        category: "",
        rating: "",
        searchValue: "",
        productsList: [],
        isLoading: false,
        activeOptionId: sortbyOptions[0].optionId,
      },
      this.getProducts
    );
  };

  renderProductsList = () => {
    const { productsList, activeOptionId } = this.state;

    const emptyList = productsList.length > 0;

    // TODO: Add No Products View

    return emptyList ? (
      <div>
        <ProductsHeader
          activeOptionId={activeOptionId}
          sortbyOptions={sortbyOptions}
          changeSortby={this.changeSortby}
        />
        <div className="all-products-container">
          <ul className="products-list">
            {productsList.map((eachProduct) => (
              <ProductCard productDetails={eachProduct} key={eachProduct.id} />
            ))}
          </ul>
        </div>
      </div>
    ) : (
      <div>
        <img
          src="https://assets.ccbp.in/frontend/react-js/nxt-trendz/nxt-trendz-no-products-view.png"
          alt="no products"
        />
        <h1 className="no-products-heading">No Products Found</h1>
        <p className="no-products-description">
          We could not find any products. Try other filters.
        </p>
      </div>
    );
  };

  renderLoader = () => (
    <div className="products-loader-container">
      <Loader type="ThreeDots" color="#0b69ff" height="50" width="50" />
    </div>
  );

  failureView = () => (
    <div>
      <img
        src="https://assets.ccbp.in/frontend/react-js/nxt-trendz/nxt-trendz-products-error-view.png"
        alt="products failure"
      />
    </div>
  );

  // TODO: Add failure view
  showViews = () => {
    const { apiStatus } = this.state;
    switch (apiStatus) {
      case apiStatusObj.inProgress:
        return this.renderLoader();
      case apiStatusObj.success:
        return this.renderProductsList();
      case apiStatusObj.failure:
        return this.failureView();
      default:
        return null;
    }
  };
  eventKey = () => {
    this.getProducts();
  };
  render() {
    return (
      <div className="all-products-section">
        {/* TODO: Update the below element */}
        <FiltersGroup
          ratingsList={ratingsList}
          categoryOptions={categoryOptions}
          userCategoryInput={this.userCategoryInput}
          userRatingInput={this.userRatingInput}
          clearAllFilters={this.clearAllFilters}
          userSearchInput={this.userSearchInput}
          eventKey={this.eventKey}
        />

        {this.showViews()}
      </div>
    );
  }
}

export default AllProductsSection;

