import { Component } from "react";

import Cookies from "js-cookie";

import Loader from "react-loader-spinner";

import ProductCard from "../ProductCard";

import "./index.css";

const apiStatusObj = {
  initial: "INITIAL",
  success: "SUCCESS",
  failure: "FAILURE",
  inProgress: "INPROGRESS",
};

class PrimeDealsSection extends Component {
  state = { productList: [], apiStatus: apiStatusObj.initial };
  componentDidMount() {
    this.getPrimeDeals();
  }

  getPrimeDeals = async () => {
    this.setState({ apiStatus: apiStatusObj.inProgress });
    const jwtToken = Cookies.get("jwt_token");
    const options = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
    };

    const response = await fetch("https://apis.ccbp.in/prime-deals", options);
    if (response.ok === true) {
      const data = await response.json();
      console.log(data.products);
      const filteredData = data.prime_deals.map((eachProducts) => ({
        title: eachProducts.title,
        brand: eachProducts.brand,
        price: eachProducts.price,
        id: eachProducts.id,
        imageUrl: eachProducts.image_url,
        ratings: eachProducts.ratings,
      }));

      this.setState({
        productList: filteredData,
        apiStatus: apiStatusObj.success,
      });
    } else if (response.status === 401) {
      this.setState({ apiStatus: apiStatusObj.failure });
    }
  };

  successView = () => {
    const { productList } = this.state;
    return (
      <div>
        <ul>
          {productList.map((eachProducts) => (
            <ProductCard productDetails={eachProducts} key={eachProducts.id} />
          ))}
        </ul>
      </div>
    );
  };

  showLoading = () => (
    <div>
      <Loader />
    </div>
  );
  failureView = () => (
    <div>
      <img
        src="https://assets.ccbp.in/frontend/react-js/exclusive-deals-banner-img.png"
        alt="Register Prime"
      />
    </div>
  );
  showView = () => {
    const { apiStatus } = this.state;
    switch (apiStatus) {
      case apiStatusObj.success:
        return this.successView();
      case apiStatusObj.failure:
        return this.failureView();
      case apiStatusObj.inProgress:
        return this.showLoading();
      default:
        return null;
    }
  };
  render() {
    return <div>{this.showView()}</div>;
  }
}

export default PrimeDealsSection;
