import "./index.css";
import CartContext from "../../context/CartContext";
import Cart from "../Cart";

const PaymentOptions = (props) => {
  const { details, UserPaymentOption } = props;
  const { id, displayText, value } = details;
  const onChangePayment = (event) => {
    UserPaymentOption(event.target.value);
  };
  //   const disabled = id !== "COD" ? "disabled" : "";

  return (
    <CartContext.Consumer>
      return (
      <li className="payment-container">
        <input
          id={id}
          name="payment"
          value={value}
          type="radio"
          onChange={onChangePayment}
          disabled={id !== "COD"}
        />
        <lable htmlFor={id} className="payment-label">
          {displayText}
        </lable>
      </li>
      );
    </CartContext.Consumer>
  );
};
export default PaymentOptions;
