import { Component } from "react";
import { Route, Switch, Redirect } from "react-router-dom";

import LoginForm from "./components/LoginForm";
import Home from "./components/Home";
import Products from "./components/Products";
import ProductItemDetails from "./components/ProductItemDetails";
import Cart from "./components/Cart";
import NotFound from "./components/NotFound";
import ProtectedRoute from "./components/ProtectedRoute";
import CartContext from "./context/CartContext";

import "./App.css";

class App extends Component {
  state = {
    cartList: [],
    paymentOption: "",
  };

  //   TODO: Add your code for remove all cart items, increment cart item quantity, decrement cart item quantity, remove cart item
  removeAllCartItems = () => {
    this.setState({ cartList: [] });
  };

  incrementCartItemQuantity = (id) => {
    this.setState((prevState) => ({
      cartList: prevState.cartList.map((eachItem) => {
        if (eachItem.id === id) {
          const updatedQuantity = eachItem.quantity + 1;
          return { ...eachItem, quantity: updatedQuantity };
        } else {
          return eachItem;
        }
      }),
    }));
  };

  decrementCartItemQuantity = (id) => {
    const { cartList } = this.state;
    const productObj = cartList.find((eachItem) => eachItem.id === id);
    if (productObj.quantity > 1) {
      this.setState((prevState) => ({
        cartList: prevState.cartList.map((eachItem) => {
          if (eachItem.id === id) {
            const updatedQuantity = eachItem.quantity - 1;
            return { ...eachItem, quantity: updatedQuantity };
          } else {
            return eachItem;
          }
        }),
      }));
    } else {
      this.removeCartItem(id);
    }
  };

  addCartItem = (product) => {
    const { cartList } = this.state;
    const isExist = cartList.find((eachItem) => eachItem.id === product.id);
    if (isExist) {
      this.incrementCartItemQuantity(product.id);
    } else {
      this.setState((prevState) => ({
        cartList: [...prevState.cartList, product],
      }));
    }

    //   TODO: Update the code here to implement addCartItem
  };

  removeCartItem = (id) => {
    this.setState((prevState) => ({
      cartList: prevState.cartList.filter((eachItem) => eachItem.id !== id),
    }));
  };
  confirmOrder = (option) => {
    this.setState({ paymentOption: option });
  };

  render() {
    const { cartList, paymentOption } = this.state;

    return (
      <CartContext.Provider
        value={{
          cartList,
          paymentOption,
          addCartItem: this.addCartItem,
          removeCartItem: this.removeCartItem,
          removeAllCartItems: this.removeAllCartItems,
          decrementCartItemQuantity: this.decrementCartItemQuantity,
          incrementCartItemQuantity: this.incrementCartItemQuantity,
          confirmOrder: this.confirmOrder,
        }}
      >
        <Switch>
          <Route exact path="/login" component={LoginForm} />
          <ProtectedRoute exact path="/" component={Home} />
          <ProtectedRoute exact path="/products" component={Products} />
          <ProtectedRoute
            exact
            path="/products/:id"
            component={ProductItemDetails}
          />
          <ProtectedRoute exact path="/cart" component={Cart} />
          <Route path="/not-found" component={NotFound} />
          <Redirect to="not-found" />
        </Switch>
      </CartContext.Provider>
    );
  }
}

export default App;
