import { Component } from "react";

import Cookies from "js-cookie";

import Loader from "react-loader-spinner";

import "./index.css";

const apiStatusConstants = {
  initial: "INITIAL",
  success: "SUCCESS",
  failure: "FAILURE",
  inProgress: "INPROGRESS",
};

class ProductItemDetails extends Component {
  state = {
    specificProduct: {},
    similarProducts: [],
    count: 1,
    apiStatus: apiStatusConstants.initial,
  };
  componentDidMount() {
    const { match } = this.props;
    const { params } = match;
    // console.log(params.id);
    this.getSpecifiedProduct(params.id);
  }

  covertToCamelCase = (data) => {
    return {
      availability: data.availability,
      brand: data.brand,
      description: data.description,
      imageUrl: data.image_url,
      price: data.price,
      rating: data.rating,
      title: data.title,
      totalReviews: data.total_reviews,
    };
  };
  getSpecifiedProduct = async (id) => {
    this.setState({ apiStatus: apiStatusConstants.inProgress });
    const jwtToken = Cookies.get("jwt_token");
    const apiUrl = `https://apis.ccbp.in/products/${id}`;
    const options = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
      method: "GET",
    };
    const response = await fetch(apiUrl, options);
    if (response.ok === true) {
      const data = await response.json();
      console.log(data);
      const updatedSpecificProduct = this.covertToCamelCase(data);
      const updatedSimilarProductsList = data.similar_products.map((product) =>
        this.covertToCamelCase(product)
      );
      this.setState(
        {
          specificProduct: updatedSpecificProduct,
          similarProducts: updatedSimilarProductsList,
          apiStatus: apiStatusConstants.success,
        },
        this.successView
      );
    } else {
      this.setState(
        { apiStatus: apiStatusConstants.failure },
        this.failureView
      );
    }
  };
  loadingView = () => (
    <div>
      <Loader />
    </div>
  );
  onClickReturnToProductsPage = () => {
    const { history } = this.props;
    history.replace("/products");
  };
  failureView = () => (
    <div>
      <img
        src="https://assets.ccbp.in/frontend/react-js/nxt-trendz-error-view-img.png"
        alt="error view"
        className="failureImageSize"
      />
      <h1>Product Not Found</h1>
      <button type="button" onClick={this.onClickReturnToProductsPage}>
        Continue Shopping
      </button>
    </div>
  );
  showSimilarProducts = (similarProducts) => {
    return (
      <div>
        <h1>Similar Products</h1>
        <ul>
          {similarProducts.map((eachProduct) => (
            <li>
              <img src={eachProduct.imageUrl} alt={eachProduct.title} />
              <p></p>
            </li>
          ))}
        </ul>
      </div>
    );
  };
  successView = () => {
    const { specificProduct, similarProducts, count } = this.state;

    const {
      availability,
      brand,
      description,
      imageUrl,
      price,
      rating,
      title,
      totalReviews,
    } = specificProduct;
    return (
      <div>
        <div>
          <img src={imageUrl} alt={title} />
          <div>
            <h1>{title}</h1>
            <p>{price}</p>
            <div>
              <p>{rating}</p>
              <img
                src="https://assets.ccbp.in/frontend/react-js/star-img.png"
                alt="star"
              />
              <p>{totalReviews}</p>
            </div>
            <p>{description}</p>
            <p>Availability: {availability}</p>
            <p>Brand:{brand}</p>
            <div>
              <button type="button">-</button>
              <p>{count}</p>
              <button type="button">+</button>
            </div>
          </div>
        </div>
        <div>{this.showSimilarProducts(similarProducts)}</div>
      </div>
    );
  };
  showView = () => {
    const { apiStatus } = this.state;

    switch (apiStatus) {
      case apiStatusConstants.success:
        return this.successView();
      case apiStatusConstants.failure:
        return this.failureView();
      case apiStatusConstants.inProgress:
        return this.loadingView();
      default:
        return null;
    }
  };
  render() {
    return <div>{this.showView()}</div>;
  }
}

export default ProductItemDetails;
