const ProductHeader = (props) => {
  const {
    sortbyOptions,
    updateSortByValue,
    userSearchInput,
    sortByValue,
  } = props;

  const onChangeOption = (event) => updateSortByValue(event.target.value);
  let search = "";
  const onChangeSearchInput = (event) => {
    console.log(event.target.value);
    if (event.key === "Enter") {
      userSearchInput(search);
    } else {
      search = event.target.value;
    }
  };
  return (
    <div>
      <div>
        <input type="search" onKeyPress={onChangeSearchInput} />
      </div>
      <div>
        <p>Sort by</p>
        <select onChange={onChangeOption} value={sortByValue}>
          {sortbyOptions.map((eachOption) => (
            <option value={eachOption.optionId} key={eachOption.optionId}>
              {eachOption.displayText}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default ProductHeader;
